import { createApp } from "vue";
import App from "./App.vue";
import router from "./router";
import mixins from "./mixins";
import i18n from "./plugins/i18n";

const app = createApp(App);
app.use(router);
app.mixin(mixins);
app.mount("#app");
app.directive("focus", {
  mounted(el) {
    el.focus();
  },
});
app.use(i18nPlugin, i18nStrings);

const i18nStrings = {
  en: {
    hi: "Hello!",
  },
  ko: {
    hi: "안녕하세요!",
  },
};
